sbt -batch -java-home /usr/lib/jvm/openjdk-8 -Dmacro.settings=print-codecs clean 'jsoniter-scala-benchmark/jmh:run -p size=128 -prof gc -rf json -rff openjdk-8.json .*' 2>&1 | tee openjdk-8.txt
